function [output_signal, iterations] = processing_GLA_phase(input_signal, iterations)
    %% README
    
    % This processing block reconstructs the input signal with a phase
    % generated by the Griffin and Lim Algorithm starting with a completely
    % randomized phase.
    
    %% Script
    if nargin == 1
       iterations = floor(random_number_generator([1 100])); 
    end
    % Initialize
    win_length = 512;
    hop_length = win_length/4;
    
    % Acquire the magnitude only
    spectrogram = STFT_DFT(input_signal, win_length, hop_length);
    signal_mag = abs(spectrogram);
    
    % Run function
    output_signal = GLA(signal_mag, win_length, hop_length, iterations);
end

